/**
 * Creates a $project pipeline stage for reshaping documents.
 *
 * This is equivalent to MongoDB's $project aggregation operator. Unlike the top-level `projection` field which applies only to the final output, $project stages can be used at any point in the pipeline.
 *
 * @param projection - Projection specification with field selections and computed values
 *
 * @returns A $project stage
 *
 * @example
 * ```typescript
 * // Include specific fields
 * const includeStage = project({
 *   _id: 1,
 *   name: 1,
 *   email: 1
 * });
 * // { $project: { _id: 1, name: 1, email: 1 } }
 *
 * // Rename and compute fields
 * const computedStage = project({
 *   userId: '$_id',
 *   fullName: { $concat: ['$firstName', ' ', '$lastName'] },
 *   isActive: { $eq: ['$status', 'active'] }
 * });
 * // { $project: { userId: '$_id', fullName: ..., isActive: ... } }
 *
 * // Complex transformations
 * const complexStage = project({
 *   compositeId: { $concat: ['$accountId', '_', '$userId'] },
 *   accountRef: '$accountId',
 *   metadata: {
 *     createdAt: '$createdAt',
 *     updatedAt: '$updatedAt'
 *   }
 * });
 * ```
 *
 */

/**
 * Represents a MongoDB $project aggregation stage.
 *
 * The $project stage reshapes documents by: - Including/excluding fields - Adding computed fields - Renaming fields - Creating new fields from expressions
 *
 * Unlike SimpleTableMapping projection which applies only to the final output, $project stages can be used at any point in the pipeline to transform documents before subsequent stages.
 *
 * @template T - The document type (for type hints only, not enforced at runtime)
 *
 * @example
 * ```typescript
 * // Include specific fields
 * const stage: ProjectStage = {
 *   $project: {
 *     _id: 1,
 *     name: 1,
 *     email: 1
 *   }
 * };
 *
 * // Exclude fields
 * const excludeStage: ProjectStage = {
 *   $project: {
 *     password: 0,
 *     internalNotes: 0
 *   }
 * };
 *
 * // Rename and compute fields
 * const computedStage: ProjectStage = {
 *   $project: {
 *     _id: 1,
 *     userId: '$_id',
 *     fullName: { $concat: ['$firstName', ' ', '$lastName'] },
 *     isActive: { $eq: ['$status', 'active'] }
 *   }
 * };
 *
 * // Complex transformations
 * const complexStage: ProjectStage = {
 *   $project: {
 *     compositeId: { $concat: ['$accountId', '_', '$userId'] },
 *     accountRef: '$accountId',
 *     userRef: { $toObjectId: '$userId' },
 *     metadata: {
 *       createdAt: '$createdAt',
 *       updatedAt: '$updatedAt'
 *     }
 *   }
 * };
 * ```
 *
 */

